import java.util.Scanner;

public class Ex11Math {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scn = new Scanner(System.in);
		int m = scn.nextInt();
		int n = scn.nextInt();
		System.out.println(m + n);
		System.out.println(m - n);
		System.out.println(m * n);
	}

}
